import React, { Component } from "react";
import User from "./User";

import classes from "./Users.module.css";

class Users extends Component {
  //When we add the constructor to our class and we extend another class, we need to call super()
  //Which calls the constructor of the super class (the class were inheriting from)
  constructor() {
    super();
    this.state = {
      showUsers: true,
      // more: 'Test', //second state piece
    };
  }

  componentDidUpdate() {
    if (this.props.users.length === 0) {
      throw new Error("No users provided!");
    }
  }

  toggleUsersHandler() {
    // this.state.showUsers = false; //NOT!
    this.setState((curState) => {
      //what we return here will be merged with the old state so the second state piece will not be lost
      return { showUsers: !curState.showUsers };
    });
  }

  render() {
    const usersList = (
      <ul>
        {this.props.users.map((user) => (
          <User key={user.id} name={user.name} />
        ))}
      </ul>
    );

    return (
      <div className={classes.users}>
        <button onClick={this.toggleUsersHandler.bind(this)}>
          {this.state.showUsers ? "Hide" : "Show"} Users
        </button>
        {this.state.showUsers && usersList}
      </div>
    );
  }
}

export default Users;

